// export default form;
// import React from 'react'
import React, { useEffect, useRef, useState } from "react";
import { useLocation } from "react-router-dom";
// import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.css';
import './style/form.css'
function ProductForm() {
  const location = useLocation();
  const propsData = location.state;
  console.log(propsData);
  // console.log(props);
  const title = Object.keys(propsData).length === 0 ? "Add New Product" : "Edit Product"
  return (

    <div className="pageComponent">
      <div className="titlePage">
        {title}
      </div>
      <div className="container mt-3">
        <div className="mb-3 mt-3">
          <label for="title">Title:</label>
          <input type="text" className="form-control" id="title" placeholder="Add title..." name="title">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="category">Category:</label>
          <input type="text" className="form-control" id="category" placeholder="Add Category..." name="category">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="price">Price:</label>
          <input type="text" className="form-control" id="price" placeholder="Add Price..." name="price">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="imgurl">Image URL:</label>
          <input type="url" className="form-control" id="imgurl" placeholder="Add Image URL..." name="imgurl">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="quantity">Quantity:</label>
          <input type="number" className="form-control" id="quantity" placeholder="Add quantity" name="quantity">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="desc">Description:</label>
         <textarea ></textarea>
          {/* <input type="text" className="form-control" id="price" placeholder="Add Price..." name="price">
          </input> */}
        </div>
        <div className="form-check mb-3">
          <label className="form-check-label">
            <input className="form-check-input" type="checkbox" name="remember"></input> Remember me

          </label>
        </div>
        <button type="submit" className="btn btn-primary">Submit</button>
      </div>
    </div>
  )
}

export default ProductForm
