import React from "react";
import { Link } from "react-router-dom";
// import React, { useEffect, useRef, useState } from "react";
// import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import 'bootstrap/dist/css/bootstrap.css';
import './style/filter_line.css'
import './style/shared_page.css';
import data from '../dataForProject.js';

const filter_line = () => {
    function ProductCard({ product }) {
        return (
          <div classNameName="col">
            <Link to="/product_details"  state={product}></Link>
          <div className="card h-100">
            <img src="..." className="card-img-top" alt="..."/>
            <div className="card-body">
              <h5 className="card-title">Card title</h5>
              <p className="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
            </div>
            <div className="card-footer">
              <small className="text-muted">Last updated 3 mins ago</small>
            </div>
          </div>
        </div>
          // <div className="productCard" >
          //  <Link to="/product_details"  state={product}>
          //   <div className="image-container">
          //     <img src={product.image} alt="Image Description" />
          //   </div>
          //   </Link>
          //   <div className="data">{product.title}</div>
          //   <div className="actions">
          //     <div className="actionIcon">
          //       <i className="bi bi-trash3-fill"></i>
          //     </div>
          //     <div className="actionIcon">
          //      <Link to="/form" state={product}> <i className="bi bi-pencil-fill"></i></Link>
          //     </div>
          //   </div>
          // </div>
        );
      }
    return (
        <div>
            <div id="filterLine">
                < div id="searchBox">
                    <i className="bi bi-search"></i>
                    <input type="search" placeholder="Search"></input>
                </div>
                <div id="fiveButton" >
                    {/* פונקציה שמשנה את הצבע אם לחצו עליה- על ידי שינוי של class */}
                    <button>Electronics</button>
                    <button>Jewelery</button>
                    <button>Women</button>
                    <button>Men</button>
                    <button>All Products</button>

                </div>

            </div>
            <div id="allProducts">
                <div className="titlePage">
                    Products
                </div>
                <div id="container">
                {data.map((product) => (
        <ProductCard  product={product} />))}
                   
                </div>
            </div>
        </div>

    );
}

export default filter_line;
