import React from "react";
// import React, { useEffect, useRef, useState } from "react";
// import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './style/filter_line.css'
import './style/shared_page.css';
import data from '../dataForProject.js';

const filter_line = () => {
    function ProductCard({ product }) {
        return (
          <div className="productCard" onClick={produ}>
            <div className="image-container">
              <img src={product.image} alt="Image Description" />
            </div>
            <div className="data">{product.title}</div>
            <div className="actions">
              <div className="actionIcon">
                <i className="bi bi-trash3-fill"></i>
              </div>
              <div className="actionIcon">
                <i className="bi bi-pencil-fill"></i>
              </div>
            </div>
          </div>
        );
      }
    return (
        <div>
            <div id="filterLine">
                < div id="searchBox">
                    <i className="bi bi-search"></i>
                    <input type="search" placeholder="Search"></input>
                </div>
                <div id="fiveButton">
                    {/* פונקציה שמשנה את הצבע אם לחצו עליה- על ידי שינוי של class */}
                    <button>Electronics</button>
                    <button>Jewelery</button>
                    <button>Women</button>
                    <button>Men</button>
                    <button>All Products</button>

                </div>

            </div>
            <div id="allProducts">
                <div className="titlePage">
                    Products
                </div>
                <div id="container">
                {data.map((product) => (
        <ProductCard  product={product} />))}
                   
                </div>
            </div>
        </div>

    );
}

export default filter_line;
