import React from "react";
// import React, { useEffect, useRef, useState } from "react";
// import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import './style/filter_line.css'
const filter_line = () => {
    const data =
    {
        id: 1,
        title: 'Fjallraven - Foldsack No. 1 Backpack, Fits 15 Laptops',
        price: 109.95,
        description: 'Your perfect pack for everyday use and walks in the forest. Stash your laptop (up to 15 inches) in the padded sleeve, your everyday',
        category: "men's clothing",
        image: "https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg",
        rating: { rate: 3.9, count: 120 }
    }
    return (
        <div>
            <div id="filterLine">
                < div id="searchBox">
                    <i className="bi bi-search"></i>
                    <input type="search" placeholder="Search"></input>
                </div>
                <div id="fiveButton">
                    {/* פונקציה שמשנה את הצבע אם לחצו עליה- על ידי שינוי של class */}
                    <button>Electronics</button>
                    <button>Jewelery</button>
                    <button>Women</button>
                    <button>Men</button>
                    <button>All Products</button>

                </div>

            </div>
            <div id="titleProducts">
                Products
            </div>
            <div id="container">
                {/* קריאה לקומפוננטת בן בלולאה שמקבלת את הפרטים על המוצר ומחזירה דיב מעוצב */}
                <div id="productCard">
                    <div class="image-container">
                 
                    <img src="https://fakestoreapi.com/img/81fPKd-2AYL._AC_SL1500_.jpg" alt="Image Description"></img>
                    </div>
                    <div id="data" >
                        {data.title}
                    </div>
                    <div id="actions">
                        div
                        <i className="bi bi-trash3-fill"></i>
                        <i className="bi bi-pencil-fill"></i>

                    </div>
                </div>
            </div>
        </div>

    );
}

export default filter_line;
