// export default form;
// import React from 'react'
import React, { useEffect, useRef, useState } from "react";
import { useLocation } from "react-router-dom";
// import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.css';
import './style/form.css'
import data from '../dataForProject.js';

function ProductForm() {
  const location = useLocation();
  const propsData = location.state;
  console.log(propsData);
  // console.log(props);
  const product= useRef();
  product=propsData;
  const [title,action,flag] = Object.keys(propsData).length === 0 ? ["Add New Product","ADD" ,true]: ["Edit Product","EDIT",false]
  
  return (

    <div className="pageComponent">
      <div className="titlePage">
        {title}
      </div>
      <div className="container mt-3">
        <div className="mb-3 mt-3">
          <label for="title">Title:</label>
          <input ref={product.title} type="text" defaultValue={flag ? "" : propsData.title} className="form-control" id="title"  placeholder= {flag ?"Add title..." :propsData.title }  name="title">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="category">Category:</label>
          <input ref={product.category} type="text" defaultValue={flag ? "" : propsData.category} className="form-control" id="category" placeholder={flag ?"Add Category..." :propsData.category }  name="category">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="price">Price:</label>
          <input ref={product.price} type="text" defaultValue={flag ? "" : propsData.price} className="form-control" id="price" placeholder={flag ?"Add Price..." :propsData.price } name="price">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="imgurl">Image URL:</label>
          <input ref={product.image} type="url" defaultValue={flag ? "" : propsData.image} className="form-control" id="imgurl" placeholder= {flag ?"Add Image URL..." :propsData.image } name="imgurl">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="quantity">Quantity:</label>
          <input ref={product.quantity} type="number" defaultValue={flag ? "" : propsData.quantity} className="form-control" id="quantity" placeholder= {flag ?"Add quantity" :propsData.quantity } name="quantity">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="description">Description:</label>
         <textarea  className="form-control" id="description" defaultValue={flag ? "" : propsData.description} placeholder= {flag ?"Add Description..." :propsData.description } name="description"></textarea>
          {/* <input type="text" className="form-control" id="price" placeholder="Add Price..." name="price">
          </input> */}
        </div>
        {/* <div className="form-check mb-3">
          <label className="form-check-label">
            <input className="form-check-input" type="checkbox" name="remember"></input> Remember me

          </label>
        </div> */}
        <button type="submit" className="btn btn-primary custom-button">{action}</button>
      </div>
    </div>
  )
}

export default ProductForm
