import React from 'react';
import Filter_line from './filter_line';
import All_products from './all_products';
import { useState, useEffect } from 'react';
import data from '../dataForProject.js';

export default function BodyComp() {
  const [filterInfo, setFilterInfo] = useState("");
  const [selectedButton, setSelectedButton] = useState("");

  useEffect(() => {
    if (!("quantity" in data[0]))
      data.forEach(item => {
        item.quantity = Math.floor(Math.random() * 20);
      });
  }, []);

  const handleDeleteProduct = (productId) => {
    const index = data.findIndex(p => p.id === productId);
    data.splice(index, 1);
    setFilterInfo("");
    setSelectedButton("");
  };

  return (
    <div>
      <Filter_line setFilterInfo={setFilterInfo} setSelectedButton={setSelectedButton} />
      <All_products filterInfo={filterInfo} selectedButton={selectedButton} handleDeleteProduct={handleDeleteProduct} />
    </div>
  );
}
