import React, { useRef } from "react";
import { Link } from "react-router-dom";
// import React, { useEffect, useRef, useState } from "react";
// import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import 'bootstrap/dist/css/bootstrap.css';
import './style/filter_line.css'
import './style/shared_page.css';
import data from '../dataForProject.js';

const filter_line = ({ updateFilterInfo }) => {
const inputSearchRef= useRef();
const handleClick = () => {
  const newInfo = 'Some information to pass';
  updateFilterInfo(newInfo);
};
    return (
        <div>
            <div id="filterLine">
                < div id="searchBox">
                    <i className="bi bi-search"></i>
                    <input ref={inputSearchRef} type="search" placeholder="Search"></input>
                </div>
                <div id="fiveButton" >
                    {/* פונקציה שמשנה את הצבע אם לחצו עליה- על ידי שינוי של class */}
                    <button onClick={() => handleClick('Button 1')}>Electronics</button>
                    <button value="jewelery">Jewelery</button>
                    <button value="women's clothing">Women</button>
                    <button value="men's clothing">Men</button>
                    <button value="all">All Products</button>

                </div>

            </div>

        </div>

    );
}

export default filter_line;
