        <div>
            <div id="filterLine">
                < div id="searchBox">
                    <i className="bi bi-search"></i>
                    <input value={inputValue} onChange={handleInputChange} type="search" placeholder="Search"></input>
                </div>
                <div id="fiveButton" >
                    {/* פונקציה שמשנה את הצבע אם לחצו עליה- על ידי שינוי של class */}
                    <button onClick={() => handleClick("electronics")}>Electronics</button>
                    <button onClick={() => handleClick("jewelery")}>Jewelery</button>
                    <button onClick={() => handleClick("women's clothing")}>Women</button>
                    <button onClick={() => handleClick("men's clothing")}>Men</button>
                    <button onClick={() => handleClick("all")}>All Products</button>

                </div>

            </div>

        </div>

    );
}

export default Filter_line;
