import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import data from '../dataForProject';
import "./style/all_products.css"
// import "./style/shared_page.css"
import { useEffect } from 'react';

function All_products({filterInfo }) {
    const [filterData,setFilterData]=useState(data);
    const array=[];
    const deleteProduct=(prouctToDelete)=>{
        setFilterData(data.filter(obj =>prouctToDelete.id!= (obj.id)) );
    }
    useEffect(() => {
        

        {data.map((product) => {
            if (product.title.includes(filterInfo) || product.category.includes(filterInfo) || filterInfo=="all") {
              array.push(product)
            }
          //   return null; // לא מחזיר רכיב אם התנאי לא מתקיים
          }
          )}
          setFilterData(array)

        console.log('Filter Info:', filterInfo);
      }, [filterInfo]);
    function ProductCard({ product }) {
        return (
          <div className="productCard" >
           <Link to="/product_details"  state={product}>
            <div className="image-container">
              <img src={product.image} alt="Image Description" />
            </div>
            </Link>
            <div className="data">{product.title}</div>
            <div className="actions">
              <div className="actionIcon">
                <i className="bi bi-trash3-fill"></i>
              </div>
              <div className="actionIcon">
               <Link to="/form" state={product}> <i className="bi bi-pencil-fill"></i></Link>
              </div>
            </div>
          </div>
        );
      }
  return (
    <div id="allProducts">
    <div className="titlePage">
        Products
    </div>
    <div id="container">
    {filterData.map((product) => (
<ProductCard  product={product} />))}
       {/* {filterData.map((product) => {
  if (product.title.includes(filterInfo) || product.description.includes(filterInfo) || filterInfo=="all") {
    return <ProductCard product={product} />;
  }
//   return null; // לא מחזיר רכיב אם התנאי לא מתקיים
})} */}
    </div>
</div>  )
}

export default All_products
