import React from "react";
// import React, { useEffect, useRef, useState } from "react";
// import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import 'bootstrap/dist/css/bootstrap.css';
import './style/product_details.css'
import './style/shared_page.css'
import { useLocation } from "react-router-dom";

const Product_details = () => {
    const location=useLocation();
    const propsData=location.state;
    //פה צריך לקבל מידע מקומפוננטת האבא
    return (
        <div id="product_details_page" className="pageComponent">
            <div className="titlePage">
                Product page
            </div>
            {/* <div id="container">
                <div id="productImgContainer">
                    <img>
                    </img>
                </div> */}
                {/* <div id="productData">
                    <div>
                        Title
                    </div>
                    <div>
                        {product.title}
                    </div>
                    <div>
                        Description
                    </div>
                    <div>
                        {product.description}
                    </div> <div>
                        Category
                    </div>
                    <div>
                        {product.category}
                    </div> <div>
                        Price
                    </div>
                    <div>
                        {product.price}
                    </div> <div>
                        Quantity
                    </div>
                    <div>
                        {product.quantity}
                    </div> */}


                {/* </div> */}
            {/* </div> */}

            {/* <div class="card flex-row"><img class="card-img-left example-card-img-responsive" src="https://fakestoreapi.com/img/71YAIFU48IL._AC_UL640_QL65_ML3_.jpg"/>
  <div class="card-body">
    <h4 class="card-title h5 h4-sm">Left image</h4>
    <p class="card-text">Example text</p>
  </div>
</div> */}
<div classN="card mb-3 con container" >
  <div class="row no-gutters">
    <div class="col-md-4">
      <img src={propsData.image} class="card-img" alt="..."/>
    </div>
    <div class="col-md-8 ProductData">
      <div class="card-body">
        <h5 class="card-title">Title</h5>
        <p class="card-text">{propsData.title}</p>
        <h5 class="card-title">Description</h5>
        <p class="card-text">{propsData.description}</p>
        <h5 class="card-title">Category</h5>
        <p class="card-text">{propsData.category}</p>
        <h5 class="card-title">price</h5>
        <p class="card-text">{propsData.price}</p>
        <h5 class="card-title">Quantity</h5>
        <p class="card-text">{propsData.quantity}</p>
        {/* <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p> */}
      </div>
    </div>
  </div>
</div>
        </div>

    );
}

export default Product_details;
