import React, { useState } from 'react'
import { Link } from 'react-router-dom';
import data from '../dataForProject';
import "./style/all_products.css"
 import "./style/shared_page.css"
 import 'bootstrap/dist/css/bootstrap.css';

import { useEffect } from 'react';
//************************************************************************************************ */
function All_products({ filterInfo }) {
  const [filterData, setFilterData] = useState(data);
//   const deleteProduct = (product) => {
// const index= data.findIndex(p=>p.id==product.id);
// data.splice(index,1);
// console.log(data);
// setFilterData(data);
// console.log(filterData);
//     // const updatedData = data.filter((item) => item !== product);
//     // setFilterData(updatedData);
//     // data=updatedData;
//   };

const handleDeleteProduct = (productId) => {
  // const updatedFilterData = filterData.filter(
  //   (product) => product.id !== productId
  // );

  const index= data.findIndex(p=>p.id==productId);
data.splice(index,1);
console.log(data);
setFilterData([...data])
console.log(filterData);
//   setFilterData(data);
};
useEffect(() => {
  filterInfo="";

  // This effect will run when filterData changes
  console.log('filterData has changed:', filterData);
}, [filterData]);
  useEffect(() => {
    // setFilterData(data)

    const filteredProducts = data.filter(
      (product) =>
        product.title.toLocaleLowerCase().includes(filterInfo) || filterInfo === ""||(
        product.category.toLocaleLowerCase().includes(filterInfo) && filterInfo!="men's clothing" )
        ||  product.category===filterInfo
       
    );
    setFilterData([...filteredProducts]);

  }, [filterInfo]);


  function ProductCard({ product,onDelete  }) {

    const handleDelete = () => {
      onDelete(product.id);
    };
    // const randNumber=0;
    //************************************************************************************************ */
    return (
      <div className="productCard" >
        <Link to="/product_details" state={product}>
          <div className="image-container">
            <img src={product.image} alt={product.description} />
          </div>
        </Link>
        <div className="data">{product.title}</div>
        <div  className="actions quantityIcons">
        <div className="actionIcon icons quantityIcon" >
          <p className='hoverIcon' onClick={()=>{product.quantity++}}>+1</p>
          
          </div>
          <div className="actionIcon icons quantityIcon" >
           <p className='hoverIcon quty'> { product.quantity}</p>
          </div>
          <div className="actionIcon icons quantityIcon" >
          <p className='hoverIcon'> -1</p> 
          </div>
          </div>
        <div className="actions">
          <div className="actionIcon" onClick={handleDelete}>
            <i className="bi bi-trash3-fill icons hoverIcon"  ></i>
          </div>
          <div className="actionIcon">
            <Link to="/form" state={product}> <i className="icons bi bi-pencil-fill custom-link hoverIcon"></i></Link>
          </div>
        </div>
      </div>
    );
  }
  return (
    <div id="allProducts" className='pageComponent withFilter'>
      <div className="titlePage">
        Products
      </div>
      <div id="container">
        {filterData.map((product) => (
          <ProductCard             key={product.id}
          product={product}
          onDelete={handleDeleteProduct}/>))}
      </div>
    </div>)
}

export default All_products
