import React, { useRef, useState,useEffect } from "react";
import { Link } from "react-router-dom";
// import React, { useEffect, useRef, useState } from "react";
// import 'bootstrap/dist/css/bootstrap.css';
import 'bootstrap-icons/font/bootstrap-icons.css';
import 'bootstrap/dist/css/bootstrap.css';
import './style/filter_line.css'
import './style/shared_page.css';
import data from '../dataForProject.js';

const Filter_line = ({ setFilterInfo }) => {
  const [inputSearch, setInputSearch] = useState('');
  const [wordSearch, setWordSearch] = useState('');

  const handleClick = (wordFilter) => {
    setFilterInfo(wordFilter);
  };
  
  const handleIconClick = () => {
    setWordSearch(inputSearch);
    updateFilterInfo(wordSearch);
  };


  const handleInputChange = (event) => {
    setInputSearch(event.target.value)
  }
  useEffect(() => {
      updateFilterInfo(wordSearch);
    
  }, [updateFilterInfo]);
  return (
    <div>
      <div id="filterLine">
        < div id="searchBox">
          <i onClick={handleIconClick} className="bi bi-search"></i>
          <input value={inputSearch} onChange={handleInputChange} type="search" placeholder="Search"></input>
        </div>
        <div id="fiveButton" >
          {/* פונקציה שמשנה את הצבע אם לחצו עליה- על ידי שינוי של class */}
          <button onClick={() => handleClick("electronics")}>Electronics</button>
          <button onClick={() => handleClick("jewelery")}>Jewelery</button>
          <button onClick={() => handleClick("women's clothing")}>Women</button>
          <button onClick={() => handleClick("men's clothing")}>Men</button>
          <button onClick={() => handleClick("all")}>All Products</button>

        </div>

      </div>

    </div>

  );
}

export default Filter_line;
