// export default form;
// import React from 'react'
import React, { useEffect, useRef, useState } from "react";
import { useLocation } from "react-router-dom";
// import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/css/bootstrap.css';
import './style/form.css'
import data from '../dataForProject.js';

function ProductForm() {
  const location = useLocation();
  const propsData = location.state;
  console.log(propsData);
  cost [flag,setflag]=useState(true);
  const [title,settitle ]=useState("");
  const [acttion,setaction ]=useState("")
  [aettitle,setaction ] = Object.keys(propsData).length === 0 ? ["Add New Product","ADD" ,setflag(true)]: ["Edit Product","EDIT",setflag(false)]
  const titleRef = useRef("");
  const categoryRef = useRef("");
  const priceRef = useRef("");
  const imageRef = useRef("");
  const quantityRef = useRef("");
  const descriptionRef = useRef("");

  
  useEffect(() => {
    initializeInputValues();
  }, []);
  const initializeInputValues = () => {
    titleRef.current = flag ? "" : propsData.title;
    categoryRef.current = flag ? "" : propsData.category;
    priceRef.current = flag ? "" : propsData.price;
    imageRef.current = flag ? "" : propsData.image;
    quantityRef.current = flag ? "" : propsData.quantity;
    descriptionRef.current = flag ? "" : propsData.description;
  };


  const saveChanges = () => {
    const product = {
      title: titleRef.current,
      category: categoryRef.current,
      price: priceRef.current,
      image: imageRef.current,
      quantity: quantityRef.current,
      description: descriptionRef.current,
    };
    console.log(product);
  };
  return (
  
    <div className="pageComponent">
      <div className="titlePage">
        {title}
      </div>
      <div className="container mt-3">
        <div className="mb-3 mt-3">
          <label for="title">Title:</label>
          <input ref={titleRef} type="text" defaultValue={flag ? "" : propsData.title} className="form-control" id="title"  placeholder= {flag ?"Add title..." :propsData.title }  name="title">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="category">Category:</label>
          <input ref={categoryRef} type="text" defaultValue={flag ? "" : propsData.category} className="form-control" id="category" placeholder={flag ?"Add Category..." :propsData.category }  name="category">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="price">Price:</label>
          <input ref={priceRef} type="text" defaultValue={flag ? "" : propsData.price} className="form-control" id="price" placeholder={flag ?"Add Price..." :propsData.price } name="price">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="imgurl">Image URL:</label>
          <input ref={imageRef} type="url" defaultValue={flag ? "" : propsData.image} className="form-control" id="imgurl" placeholder= {flag ?"Add Image URL..." :propsData.image } name="imgurl">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="quantity">Quantity:</label>
          <input ref={quantityRef} type="number" defaultValue={flag ? "" : propsData.quantity} className="form-control" id="quantity" placeholder= {flag ?"Add quantity" :propsData.quantity } name="quantity">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="description">Description:</label>
         <textarea ref={descriptionRef} className="form-control" id="description" defaultValue={flag ? "" : propsData.description} placeholder= {flag ?"Add Description..." :propsData.description } name="description"></textarea>
          {/* <input type="text" className="form-control" id="price" placeholder="Add Price..." name="price">
          </input> */}
        </div>
        {/* <div className="form-check mb-3">
          <label className="form-check-label">
            <input className="form-check-input" type="checkbox" name="remember"></input> Remember me

          </label>
        </div> */}
        <button onClick={saveChanges} type="submit" className="btn btn-primary custom-button">{action}</button>
      </div>
    </div>
  )
}

export default ProductForm
