//   };
const [title, action, flag] = Object.keys(propsData).length === 0 ? ["Add New Product", "ADD", true] : ["Edit Product", "EDIT", false];
const [titleValue, setTitleValue] = useState("");
const [categoryValue, setCategoryValue] = useState("");
const [priceValue, setPriceValue] = useState("");
const [imageValue, setImageValue] = useState("");
const [quantityValue, setQuantityValue] = useState("");
const [descriptionValue, setDescriptionValue] = useState("");

useEffect(() => {
  if (!flag) {
    setTitleValue(propsData.title);
    setCategoryValue(propsData.category);
    setPriceValue(propsData.price);
    setImageValue(propsData.image);
    setQuantityValue(propsData.quantity);
    setDescriptionValue(propsData.description);
  }
}, [flag, propsData]);

const saveChanges = () => {
  const product = {
    title: titleValue,
    category: categoryValue,
    price: priceValue,
    image: imageValue,
    quantity: quantityValue,
    description: descriptionValue,
  };
  console.log(product);
};

  return (
  
    <div className="pageComponent">
      <div className="titlePage">
        {title}
      </div>
      <div className="container mt-3">
        <div className="mb-3 mt-3">
          <label for="title">Title:</label>
          <input  type="text" defaultValue={titleValue} className="form-control" id="title"  placeholder= {flag ?"Add title..." :propsData.title }  name="title">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="category">Category:</label>
          <input  type="text" defaultValue={flag ? "" : propsData.category} className="form-control" id="category" placeholder={flag ?"Add Category..." :propsData.category }  name="category">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="price">Price:</label>
          <input ref={priceRef} type="text" defaultValue={flag ? "" : propsData.price} className="form-control" id="price" placeholder={flag ?"Add Price..." :propsData.price } name="price">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="imgurl">Image URL:</label>
          <input ref={imageRef} type="url" defaultValue={flag ? "" : propsData.image} className="form-control" id="imgurl" placeholder= {flag ?"Add Image URL..." :propsData.image } name="imgurl">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="quantity">Quantity:</label>
          <input ref={quantityRef} type="number" defaultValue={flag ? "" : propsData.quantity} className="form-control" id="quantity" placeholder= {flag ?"Add quantity" :propsData.quantity } name="quantity">
          </input>
        </div>

        <div className="mb-3 mt-3">
          <label for="description">Description:</label>
         <textarea ref={descriptionRef} className="form-control" id="description" defaultValue={flag ? "" : propsData.description} placeholder= {flag ?"Add Description..." :propsData.description } name="description"></textarea>
          {/* <input type="text" className="form-control" id="price" placeholder="Add Price..." name="price">
          </input> */}
        </div>
        {/* <div className="form-check mb-3">
          <label className="form-check-label">
            <input className="form-check-input" type="checkbox" name="remember"></input> Remember me

          </label>
        </div> */}
        <button onClick={saveChanges} type="submit" className="btn btn-primary custom-button">{action}</button>
      </div>
    </div>
  )
}

export default ProductForm
